var host="http://"+window.location.hostname,socket=io(host,{path:"/node/socket.io"});$(function(){socket.on("connect",()=>{console.warn("connect: "+socket.id),toastr.success("ID : "+socket.id,'<i class="fa fa-exchange"></i> socket connected!',{timeOut:5e3,positionClass:"toast-top-right"})}).on("connect_error",o=>{console.warn("connect_error: "+o),toastr.error(o,'<text class="text-danger"><i class="fa fa-exclamation-triangle"></i> socket connect error!</text>',{timeOut:2e3,positionClass:"toast-top-right"})}).on("disconnect",o=>{console.warn("disconnect: "+o)}).on("connect_timeout",o=>{console.warn("connect_timeout: "+o)}).on("reconnect",o=>{console.warn("reconnect: "+o)}).on("reconnect_error",o=>{console.warn("reconnect_error: "+o)})});